package com.example.demo.Interceptor;import com.example.demo.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.annotation.Order;import org.springframework.stereotype.Component;import org.springframework.web.servlet.handler.HandlerInterceptorAdapter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;    @Component    public class AccessInterceptor extends HandlerInterceptorAdapter {        @Autowired        private UserService userService;        @Override        public boolean preHandle(HttpServletRequest request,                                 HttpServletResponse response, Object handler) throws Exception {            System.out.println("执行权限验证拦截器");            if(request.getRequestURI().equals("/user/login")||                    request.getRequestURI().equals("/user/register")||                    request.getRequestURI().equals("/user/getcode")) {                return true;            }else{                return userService.checkToken(request.getHeader("token"));            }        }}