package com.example.demo.controller;import com.example.demo.service.ArticleService;import com.example.demo.util.DateUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.Date;@RestController@RequestMapping("/article")public class ArticleController {    @Autowired    ArticleService articleService;    @PostMapping("/addarticle")    public Object addArticle(@RequestParam("title")String title,                             @RequestParam("content")String content,                             @RequestParam("userAccount")String userAccount,                             @RequestParam("time")String time                             ){        try{            return articleService.addArticle(title,content,userAccount,                    DateUtil.dateformate(time),null);        }catch (Exception e){            return e.getMessage();        }    }    @PostMapping("/addcomment")    public Object addComment(@RequestParam("content")String content,                             @RequestParam("articleId")Long articleId,                             @RequestParam("userAccount")String userAccount,                             @RequestParam("time")String time){        try{            return articleService.addComment(content,articleId,userAccount,                    DateUtil.dateformate(time),null);        }catch (Exception e){            return e.getMessage();        }    }    @PostMapping("getarticle")    public Object getarticle(@RequestParam("page")int page, @RequestParam("type")String type){        try{            return articleService.getArticle(page,type);        }catch (Exception e){            return e.getMessage();        }    }}