package com.example.demo.controller;import com.example.demo.service.ResourceService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/resource")public class ResourceController {    @Autowired    ResourceService resourceService;    @PostMapping("/getall")    public Object getAll(){        try{            return resourceService.getResource();        }catch (Exception e){            return e.getMessage();        }    }}