package com.example.demo.controller;import com.example.demo.entity.User;import com.example.demo.repository.UserRepository;import com.example.demo.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/user")public class UserController {    @Autowired    UserService userService;    @PostMapping("/register")    public Object register(@RequestParam("account")String account,                           @RequestParam("password")String password){        try{            return userService.register(account,password);        }catch(Exception e){            return e.getMessage();        }    }    @PostMapping("/login")    public Object login(@RequestParam("account")String account,                        @RequestParam("password")String password){        try{            return userService.login(account,password);        }catch (Exception e){            return e.getMessage();        }    }}