package com.example.demo.entity;import lombok.Data;import javax.persistence.*;@Entity@Datapublic class Resource {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(nullable = false)    private Long id;    @Column(name = "resource_name",nullable = false)    private String resourceName;    @Column(name = "resource_link",nullable = false)    private String resourceLink;    @Column(name = "resource_password",nullable = false)    private String resourcePassword;    @Column(name = "resource_point",nullable = false)    private int resourcePoint;}