package com.example.demo.entity;import lombok.Data;import javax.persistence.*;@Entity@Datapublic class TomPosition {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "position_id", nullable = false)    private Long positionId;    @Column(name = "position_name",nullable = false)    private String positionName;    @Column(nullable = false)    private boolean morning;    @Column(nullable = false)    private boolean afternoon;    @Column(nullable = false)    private boolean night;    @Column(name = "user_name")    private String userName;    @Column(name = "user_phone",unique = true)    private String userPhone;}