package com.example.demo.entity;import lombok.Data;import javax.persistence.*;@Entity@Datapublic class TomPosition {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "position_id", nullable = false)    private Long positionId;    @Column(name = "position_name",nullable = false)    private String positionName;    @Column(nullable = false)    private boolean morning;    @Column(nullable = false)    private boolean afternoon;    @Column(nullable = false)    private boolean night;    @Column(name = "user1_name")    private String user1Name;    @Column(name = "user1_phone",unique = true)    private String user1Phone;    @Column(name = "user2_name")    private String user2Name;    @Column(name = "user2_phone",unique = true)    private String user2Phone;    @Column(name = "user3_name")    private String user3Name;    @Column(name = "user3_phone",unique = true)    private String user3Phone;}