package com.example.demo.repository;import com.example.demo.entity.TomPosition;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.transaction.annotation.Transactional;public interface TomPositionRepository extends JpaRepository<TomPosition,Long> {    TomPosition findByUserPhone(String phone);    @Modifying    @Transactional    @Query(value ="update TomPosition set morning=false,afternoon=false,night=false,userPhone=null,userName=null where userPhone=?1")    void deletereservation(String phone);    @Modifying    @Transactional    @Query(value = "update TomPosition set morning=false,afternoon=false,night=false,userPhone=null,userName=null")    void change();}